
a = ""
b = ""

def countMutations(a, b):
    # Check if length of string a = length of string b
    if(len(a) != len(b)):
        print("!")
    else:
        mutation = 0
        for pos in range(len(a)):
            #If pos of a has not the same letter add +1 to mutation counter
            if a[pos] != b[pos]:
                mutation = mutation +1
        return mutation
print(f"Hamming Distance = {countMutations(a,b)}")
