
# Sample Dataset
# AUGGCCAUGGCGCCCAGAACUGAGAUCAAUAGUACCCGUAUUAACGGGUGA

#Sample Output
# MAMAPRTEINSTRING


dataset = ""
def readFile(filePath):
    with open(filePath, "r") as f:
        return [l.split() for l in f.readlines()]


txtfile = readFile("RNAcodontable.txt")

codonDict ={}
codonLabel = ""
codonList = []


# Create a single CodonList 
for i in txtfile:
    codonList = codonList + i
x = 0

# Create a dictionary from Codonlist, example:  "UUU: F"
for a in codonList:
    if(len(codonList)-1 > x):
        codonLabel = codonList[x]
        codonDict[codonLabel] = codonList[x+1]
        x = x+1

z = 0

# Translate RNA into Protein 1 Letter Code
proteinString = ""
for x in dataset:
    if(len(dataset)-3 >= z): #This is not really needed since we have the stop codons
        codon = dataset[z]+dataset[z+1]+dataset[z+2]
        print(codon)
        if codon == "UGA":
            break    # break here if codon is stop codon
        if codon == "UAA":
            break    # break here if codon is stop codon
        if codon == "UAG":
            break    # break here if codon is stop codon

        else:
            proteinString=proteinString+codonDict[codon]  #Build Protein String
            z = z+3

print(proteinString)
